var thing = msg.payload;

for (var R of ["1", "2"]) {
    if (thing.name == "Robot1_" + R) {
        global.set("motors_R" + R, {
            "m1": thing.m1,
            "m2": thing.m2,
            "m3": thing.m3,
            "m4": thing.m4,
            "m5": thing.m5,
            "m6": thing.m6
        });
        global.set("load_R" + R, {
            "l1": thing.l1,
            "l2": thing.l2,
            "l3": thing.l3,
            "l4": thing.l4,
            "l5": thing.l5,
            "l6": thing.l6
        });
        global.set("temp_R" + R, {
            "t1": thing.t1,
            "t2": thing.t2,
            "t3": thing.t3,
            "t4": thing.t4,
            "t5": thing.t5,
            "t6": thing.t6
        });
        global.set("data_R" + R, {
            "s": thing.s,
            "n": thing.n
        });
    }
}

if (thing.name == "WorkshiftTerminal1") {
    global.set("terminal", {
        "p": thing.p,
        "b1": thing.b1,
        "b2": thing.b2,
        "b3": thing.b3
    });
}

return msg;

global.set("selected_robot", 1);
global.set("controlled_robot", "1");
global.set("controlled_lamp", "1");
global.set("selected_limit", "loadProcMin");

global.set("lamp_mode", "auto");

for (var R in ["1", "2"]) {
    global.set("robot_status_R" + R, "waiting");
    global.set("last_robot_status_R" + R, "waiting");
}


return msg;

