return msg;

if (global.get("selected_robot") == 1) {
    var motors = global.get("motors_R1");
} else if (global.get("selected_robot") == 2) {
    var motors = global.get("motors_R2");
}

msg.payload = motors;
return msg;

if (global.get("selected_robot") == 1) {
    var motors = global.get("motors_R1");
} else if (global.get("selected_robot") == 2) {
    var motors = global.get("motors_R2");
}

var motorsGrad = {}
var c = 1;
for (var [key, value] of Object.entries(motors)) {
    if (c <= 4) {
        motorsGrad[key] = Math.round(value / 4096 * 360);
    } else {
        motorsGrad[key] = Math.round(value / 2048 * 360);
    }
    c++;
}

msg.payload = motorsGrad;
return msg;

if (global.get("selected_robot") == 1) {
    var load = global.get("load_R1");
} else if (global.get("selected_robot") == 2) {
    var load = global.get("load_R2");
}

msg.payload = load;
return msg;

if (global.get("selected_robot") == 1) {
    var load = global.get("load_R1");
} else if (global.get("selected_robot") == 2) {
    var load = global.get("load_R2");
}

var loadProc = {}
for (var [key, value] of Object.entries(load)) {
    if (value <= 1024) {
        loadProc[key] = Math.round(value / 1024 * 100);
    } else {
        loadProc[key] = Math.round((value - 1024) / 1024 * 100);
    }
}

msg.payload = loadProc;
return msg;

if (global.get("selected_robot") == 1) {
    var temp = global.get("temp_R1");
} else if (global.get("selected_robot") == 2) {
    var temp = global.get("temp_R2");
}

msg.payload = temp;
return msg;

if (global.get("selected_robot") == 1) {
    var r = "_R1";
} else if (global.get("selected_robot") == 2) {
    var r = "_R2";
}

var load = msg.payload;
var status = [];

var i = 0;
for (var [key, value] of Object.entries(load)) {
    var key_ = Object.keys(global.get("loadProcMin" + r))[i];
    if (value < global.get("loadProcMin" + r)[key_]) {
        status[i] = {payload: 2};
    } else if (value > global.get("loadProcMax" + r)[key_]) {
        status[i] = {payload: 2};
    } else if (value < global.get("loadProcCritMin" + r)[key_]) {
        status[i] = {payload: 1};
    } else if (value > global.get("loadProcCritMax" + r)[key_]) {
        status[i] = {payload: 1};
    } else {
        status[i] = {payload: 0};
    }
    i++;
}

return status;

if (global.get("selected_robot") == 1) {
    var limit = global.get("selected_limit") + "_R1";
} else if (global.get("selected_robot") == 2) {
    var limit = global.get("selected_limit") + "_R2";
}

msg.payload = global.get(limit);
return msg;

if (global.get("selected_robot") == 1) {
    var limit = global.get("selected_limit") + "_R1";
} else if (global.get("selected_robot") == 2) {
    var limit = global.get("selected_limit") + "_R2";
}

global.set(limit, msg.payload);
return msg;

if (global.get("selected_robot") == 1) {
    var r = "_R1";
} else if (global.get("selected_robot") == 2) {
    var r = "_R2";
}

var temp = msg.payload;
var status = [];

var i = 0;
for (var [key, value] of Object.entries(temp)) {
    var key_ = Object.keys(global.get("tempMin" + r))[i];
    if (value < global.get("tempMin" + r)[key_]) {
        status[i] = {payload: 2};
    } else if (value > global.get("tempMax" + r)[key_]) {
        status[i] = {payload: 2};
    } else if (value < global.get("tempCritMin" + r)[key_]) {
        status[i] = {payload: 1};
    } else if (value > global.get("tempCritMax" + r)[key_]) {
        status[i] = {payload: 1};
    } else {
        status[i] = {payload: 0};
    }
    i++;
}

return status;

var terminal = global.get("terminal");

if (terminal["p"] == 1) {
    for (var R in ["1", "2"]) {
        if (global.get("robot_status_R" + R) != "locked") {
            global.set("last_robot_status_R" + R, global.get("robot_status_R" + R));
        }
        global.set("robot_status_R" + R, "locked");
    }
    global.set("terminal_colors", ["red"]);
} else {
    for (var R in ["1", "2"]) {
        global.set("robot_status_R" + R, global.get("last_robot_status_R" + R));
    }
}

msg.payload = terminal;
return msg;

var L = global.get("controlled_lamp");
var colors = msg.payload;

if (global.get("lamp_mode") == "manual") {
    global.set("lamp_colors_L" + L, colors);
}

msg.payload = colors;
return msg;

var L = global.get("controlled_lamp");

msg.payload = global.get("lamp_colors_L" + L);
return msg;

if (global.get("lamp_mode") == "auto") {
    var R = global.get("controlled_robot");
    var colors = [];

    switch (global.get("robot_status_R" + R)) {
        case "waiting":
            colors = ["green"];
            break;
        case "moving":
            colors = ["blue"];
            break;
        case "parking":
            colors = ["yellow"];
            break;
        case "locked":
            colors = ["red"];
            global.set("terminal_colors", colors);
            break;
        case "stopped":
            colors = ["red"];
            break;
    }

    global.set("lamp_colors_L" + R, colors);
    return msg;
}

var colors = msg.payload;

global.set("terminal_colors", colors);

msg.payload = colors;
return msg;

var R = global.get("controlled_robot");

msg.payload = global.get("coordinates_limit_R" + R);
return msg;

var R = global.get("controlled_robot");

global.set("coordinates_limit_R" + R, msg.payload);
return msg;

var R = global.get("controlled_robot");
var position = msg.payload + "_R" + R;

global.set("selected_position_R" + R, position);

return msg;

var R = global.get("controlled_robot");
var selected_position = global.get("selected_position_R" + R);
var coords = msg.payload;
var coords_limit = global.get("coordinates_limit_R" + R);

for (var [key, value] of Object.entries(coords_limit)) {
    if (coords[key] > coords_limit[key]) {
        coords[key] = selected_position[key];
        msg.payload = String(key) + " координата выходит за рамки допустимых значений!";
        return msg;
    }
}

global.set(selected_position, coords);

var R = global.get("controlled_robot");
var selected_position = global.get("selected_position_R" + R);

msg.payload = global.get(selected_position);
return msg;

var R = global.get("controlled_robot");
var selected_position = global.get("selected_position_R" + R);

global.set("current_position_R" + R, selected_position);

msg.payload = global.get("current_position_R" + R);
return msg;

if (global.get("terminal")["p"] == 1) {
    msg.payload = global.get("terminal_colors");
    return msg;
}

var R = global.get("controlled_robot");
var out = global.get("out_robot_R" + R);
var position = msg.payload;

out["Robot_name"] = "Robot1_" + R;
out["X"] = String(global.get(position)["X"]);
out["Y"] = String(global.get(position)["Y"]);

global.set("out_robot_R" + R, out);
msg.payload = global.get("out_robot_R" + R);
return msg;

var R = global.get("controlled_robot");
var out = global.get("out_robot_R" + R);
var gripper = msg.payload;

out["Robot_name"] = "Robot1_" + R;
out["G"] = gripper;

global.set("out_robot_R" + R, out);
